select.execute:
default xml namespace = "http://where.yahooapis.com/v1/schema.rng";
var out = '';
var res = y.query("select * from geo.places(1) where text='" + 
                  place1 + "'").results;
var res2 = y.query("select * from geo.places(1) where text='" + 
                   place2 + "'").results;                 
var lat1 = res.place.centroid.latitude;
var lon1 = res.place.centroid.longitude;
var lat2 = res2.place.centroid.latitude;
var lon2 = res2.place.centroid.longitude;
   // thanks to http://www.johndcook.com/lat_long_distance.html
  var rho = 3960.0;
  var phi_1 = (90.0 - lat1) * Math.PI / 180.0;
  var phi_2 = (90.0 - lat2) * Math.PI / 180.0;
  var theta_1 = lon1 * Math.PI / 180.0;
  var theta_2 = lon2 * Math.PI / 180.0;
  var d = rho * Math.acos( 
    Math.sin(phi_1) * 
    Math.sin(phi_2) * 
    Math.cos(theta_1 - theta_2) +
    Math.cos(phi_1) * 
    Math.cos(phi_2) 
  );
  var miles = Math.round(d);
  var kilometers = Math.round(1.609344*d);
response.object = <distance>
<miles>{miles}</miles>
<kilometers>{kilometers}</kilometers>
{res.place}{res2.place}</distance>;
