select.execute:
var e = encodeURIComponent;
var url = 'https://www.googleapis.com/plus/v1/activities?query=' + e(query) + '&key=' + e(key);

if (language)
  url += '&language=' + e(language);
if (pageToken)
  url += '&pageToken=' + e(pageToken);
if (maxResults)
  url += '&maxResults=' + e(maxResults);
if (/best|recent/i.test(orderBy))
  url += '&orderBy' + orderBy.toLowerCase();

response.object = y.rest(url).get().response;
