select.execute:
var result = <result/>;
var page = 1;

function fixItem(item) {
    var price = parseFloat(item.price.toString().replace("$", ""));
    item.price = <price>{price.toFixed(2)}</price>;

    var shipping = item.shipping.toString();
    if (! /.*Free Shipping.*/.test(shipping)) {
        shipping = shipping.match(/\$[0-9,]\.[0-9]{2}/);
        if (shipping) {
            shipping = shipping.toString().replace("$", "");
            shipping = parseFloat(shipping);
        } else {
            shipping = 0;
        }
    } else {
        shipping = 0;
    }

    item.shipping = <shipping>{shipping.toFixed(2)}</shipping>;
    var totalPrice = price + shipping;
    item.totalPrice = <totalPrice>{totalPrice.toFixed(2)}</totalPrice>;
    return item;
}

while (true) {

    var html = y.query("select * from html where url='http://www.newegg.com/Product/ProductList.aspx?Submit=ENE&Description=" + (query) + "&Order=" + order + "&Subcategory=" + subcategory + "&Pagesize=100&Page=" + page + "' and xpath='//form[@name=\"formProductListDataGrid\"]//tr[count(td) = 4] | //*[@class=\"redtop1\"] | //div[@id=\"bcaProductContainer\"]'").results;

    if (html..div.(@id == "bcaProductContainer").length() != 0) {
        var root = html..div.(@id == "bcaProductContainer");
        var item = <item/>;
        item.name = <name>{root..div.(@id=="bcapcHeaderArea").h1.text()}</name>;

        var link = root..div.(@id=="pcraProductReview").h3..a[0].@href.toString().replace("Review", "");
        item.link = <link>{link}</link>;
        item.price = <price>{root..h3.(@['class']=="zmp").text()}</price>;
        item.shipping = <shipping>{root..dd.(@['class']=="shipping").p.text()}</shipping>;
        item.image = <image>{root..img.(@id=="pclaImagePreview").@src}</image>;

        result.item += fixItem(item);
        break;
    }

    if (html.tr.length() == 0) break;
    if (html..h3.(@['class'] == "redtop1").length() != 0) break;

    page += 1;

    for each (var node in html.tr) {
        var item = <item/>;
        item.name = <name>{node..h3.a.text()}</name>;
        item.link = <link>{node..h3.a.@href}</link>;

        var price = node..li.(@['class'] == "ckoutAmt").p.text().toString().replace("Your Price:", "");
        item.price = <price>{price}</price>;

        var shipping = node..li.(@['class'] == "green").p.text().toString();
        item.shipping = <shipping>{shipping}</shipping>;
        item.image = <image>{node..a.img.(@width == 125).@src}</image>;

        result.item += fixItem(item);
    }

    // I was always running out of time. Don't do pagination
    break;
}

response.object = result;
