select.execute:
// Include the RTM file for signing URLs
y.include("http://rtmapihelper.googlecode.com/files/rtm-0.1.js");

var signer = new RTMSigner(api_key, secret);

var params = {
	"auth_token":auth_token,
	"api_key":api_key,
	"method":method		
};
if(list_id) {
	params["list_id"] = list_id;
}
var url = signer.createURL(params);

var resp = y.rest(url).get().response;

response.object = (format == "json") ? y.xmlToJson(resp) : resp;
