/* Remove <?xml ....?> */
/* It return a valid XML, but the wrong content type */
var remove = html.search(/\?\>/);
if (remove) {
    for (i = remove; html[i] != '<'; i++);
    html = html.substr(i);
}
xml = XML(html);

